import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../App.css';

const API_BASE = 'http://localhost:5000';

function TodoApp() {
  const [todos, setTodos] = useState([]);
  const [text, setText] = useState('');
  const [showInput, setShowInput] = useState(false);

  useEffect(() => {
    axios.get(`${API_BASE}/todos`).then(res => setTodos(res.data));
  }, []);

  const addTodo = () => {
    if (!text.trim()) return;
    axios.post(`${API_BASE}/todos`, { text }).then(() => {
      setText('');
      setShowInput(false);
      axios.get(`${API_BASE}/todos`).then(res => setTodos(res.data));
    });
  };

  const cancelAdd = () => {
    setText('');
    setShowInput(false);
  };

  const toggleTodo = (id) => {
    axios.put(`${API_BASE}/todos/${id}`).then(() => {
    axios.get(`${API_BASE}/todos`).then(res => setTodos(res.data));
    });
  };

  const deleteTodo = (id) => {
    axios.delete(`${API_BASE}/todos/${id}`).then(() => {
      axios.get(`${API_BASE}/todos`).then(res => setTodos(res.data));
    });
  };

  const saveEdit = (id) => {
  axios.put(`${API_BASE}/todos/${id}`, { text: editText }).then(() => {
    setEditText("");
    setActiveTodoId(null);
    axios.get(`${API_BASE}/todos`).then(res => setTodos(res.data));
  });
};


  return (
    <>
    <div className="container">
      <h1>üìù My To-Do Lists</h1>
      {todos.length === 0 ? (
        <p>No list found.</p>
      ) : (
        <ul>
          {todos.map(todo => (
            <div key={todo.id}>
    {/* If this todo is in "edit mode" */}
    {activeTodoId === todo.id && editText !== "" ? (
      <div>
        <input 
          value={editText} 
          onChange={e => setEditText(e.target.value)} 
        />
        <button onClick={() => saveEdit(todo.id)}>Save</button>
        <button onClick={() => setActiveTodoId(null)}>Cancel</button>
      </div>
    ) : (
      // Normal display mode
      <span onClick={() => setActiveTodoId(todo.id)}>
        {todo.text}
      </span>
    )}

    {/* If menu is open for this todo */}
    {activeTodoId === todo.id && editText === "" && (
      <div>
        <button onClick={() => toggleTodo(todo.id)}>Toggle</button>
        <button onClick={() => { setEditText(todo.text); }}>Edit</button>
        <button onClick={() => setActiveTodoId(null)}>Close</button>
      </div>
    )}
  </div>
          ))}
        </ul>
      )}

      {showInput && (
        <div className="input-box">
          <input
            value={text}
            onChange={e => setText(e.target.value)}
            placeholder="Enter list name..."
          />
          <div className="btn-group">
            <button onClick={addTodo}>Save</button>
            <button onClick={cancelAdd}>Cancel</button>
          </div>
        </div>
      )}

      <button className="add-btn" onClick={() => setShowInput(true)}>‚ûï</button>
    </div>
    </>
  );
}

export default TodoApp;
